import {useState,useEffect} from "react";
import {getAllStudent,deleteStudent} from "./Client";
import {responseNotification} from "./Notification";
import {
    Layout,
    Menu,
    Breadcrumb,
    Table,
    Empty,
    Spin,
    Button, Badge, Tag, Avatar
} from 'antd';
import {
    DesktopOutlined,
    PieChartOutlined,
    FileOutlined,
    TeamOutlined,
    UserOutlined,
    LoadingOutlined,
    PlusOutlined,} from '@ant-design/icons';
import StudentDrawerForm from "./StudentDrawerForm";

import './App.css';

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;



const TheAvatar=({name})=>{
    let trimName=name.trim();
    if(trimName.length===0){
        return<Avatar icon={UserOutlined}/>
    }
    const splitName=trimName.split(" ");
    if(splitName.length===1){
        return <Avatar>{trimName.charAt(0)}</Avatar>
    }
   // console.log(splitName[splitName.length-1].charAt(0));
    return <Avatar>{splitName[0].charAt(0)}{splitName[splitName.length-1].charAt(0)}</Avatar>
}



const removeStudent=(studentID,callback)=>{
    deleteStudent(studentID).then(()=>{
        responseNotification("success","Student delete",`Student with ${studentID} was deleted`);
        callback();
    });
}

const columns =fetchStudents=> [
    {
        title: '',
        dataIndex: 'avatar',
        key: 'avatar',
        render:(text,student)=><TheAvatar name={student.name}/>
    },
    {
        title: 'Id',
        dataIndex: 'id',
        key: 'id',
    },
    {
        title: 'Name',
        dataIndex: 'name',
        key: 'name',
    },
    {
        title: 'Email',
        dataIndex: 'email',
        key: 'email',
    },
    {
        title: 'Gender',
        dataIndex: 'gender',
        key: 'gender',
    },
    {
        title: 'Action',
        key: 'action',
        render:(text,student)=>
            <Radio.Group>
                <Popconfirm
                    placement='topRight'
                    title={`Are you sure to delete ${student.name}`}
                    onConfirm={() => removeStudent(student.id, fetchStudents)}
                    okText='Yes'
                    cancelText='No'>
                    <Radio.Button value="small">Delete</Radio.Button>
                </Popconfirm>
                <Radio.Button value="small">Edit</Radio.Button>
            </Radio.Group>
    }
];

const antIcon = <LoadingOutlined style={{ fontSize: 24 }} spin />;

function App() {
    const [students,setStudents]=useState([]);
    const [collapsed,setCollapsed]=useState(false);
    const [fetching,setFetching]=useState(true);
    const [showDrawer, setShowDrawer] = useState(false);

    const fetchStudents=()=>
        getAllStudent()
            .then(res => res.json())
            .then(data=>{
                console.log(data);
                setStudents(data);
                setFetching(false);
            });
    useEffect(()=>{
        console.log("Component is Mounted");
        fetchStudents();
    },[]);


    const renderStudents=()=>{
        if(fetching){
            return <Spin indicator={antIcon} />
        }

        if(students.length<=0){
            return <Empty/>
        }
        return <>
            <StudentDrawerForm
                showDrawer={showDrawer}
                setShowDrawer={setShowDrawer}
                fetchStudents={fetchStudents}
            />
        <Table
            dataSource={students}
            columns={columns(fetchStudents)}
            bordered
            title={() =>
                <>
                    <Tag>Number of students</Tag>
                    <Badge count={students.length} className="site-badge-count-4"/>
                    <br/><br/>
                    <Button type="primary" icon={<PlusOutlined/>} size="medium"
                            onClick={() => setShowDrawer(!showDrawer)}>
                        Add Student
                    </Button>
                    </>
                    }
            pagination={{ pageSize: 10 }}
            scroll={{ y: 500 }}
            rowKey={student=>student.id}
        />
        </>
    }

    return (
        <Layout style={{ minHeight: '100vh' }}>
            <Sider collapsible collapsed={collapsed} onCollapse={setCollapsed}>
                <div className="logo" />
                <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
                    <Menu.Item key="1" icon={<PieChartOutlined />}>
                        Option 1
                    </Menu.Item>
                    <Menu.Item key="2" icon={<DesktopOutlined />}>
                        Option 2
                    </Menu.Item>
                    <SubMenu key="sub1" icon={<UserOutlined />} title="User">
                        <Menu.Item key="3">Tom</Menu.Item>
                        <Menu.Item key="4">Bill</Menu.Item>
                        <Menu.Item key="5">Alex</Menu.Item>
                    </SubMenu>
                    <SubMenu key="sub2" icon={<TeamOutlined />} title="Team">
                        <Menu.Item key="6">Team 1</Menu.Item>
                        <Menu.Item key="8">Team 2</Menu.Item>
                    </SubMenu>
                    <Menu.Item key="9" icon={<FileOutlined />}>
                        Files
                    </Menu.Item>
                </Menu>
            </Sider>
            <Layout className="site-layout">
                <Header className="site-layout-background" style={{ padding: 0 }} />
                <Content style={{ margin: '0 16px' }}>
                    <Breadcrumb style={{ margin: '16px 0' }}>
                        <Breadcrumb.Item>User</Breadcrumb.Item>
                        <Breadcrumb.Item>Bill</Breadcrumb.Item>
                    </Breadcrumb>
                    <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
                        {renderStudents()}
                    </div>
                </Content>
                <Footer style={{ textAlign: 'center' }}>By Aron</Footer>
            </Layout>
        </Layout>
    );
}

export default App;
